    private void Table_StudMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_Table_StudMouseClicked
       int rowNo = Table_Stud.getSelectedRow();
       TableModel model = Table_Stud.getModel();
       id.setText(model.getValueAt(rowNo, 0).toString());
       name.setText(model.getValueAt(rowNo, 1).toString());
       email.setText(model.getValueAt(rowNo, 2).toString());
       phone.setText(model.getValueAt(rowNo, 3).toString());
       age.setText(model.getValueAt(rowNo, 4).toString());
       univ.setText(model.getValueAt(rowNo, 5).toString());
     
    }//GEN-LAST:event_Table_StudMouseClicked

    private void modify_btnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_modify_btnActionPerformed
    Student stud = new Student();
        
        if(!id.getText().isEmpty() && !name.getText().isEmpty() && !email.getText().isEmpty() && !phone.getText().isEmpty()  &&!age.getText().isEmpty() && !univ.getText().isEmpty())
        {if(stud.validateEmail(email.getText()))  {
            try {
                PreparedStatement pstm;
                int count;

                Connection con = DriverManager.getConnection("jdbc:derby://localhost:1527/Library", "user1", "123" );
                count = 0;
                int ID= Integer.parseInt(id.getText());
                String Name = name.getText();
                String Email = email.getText();
                String Phone = phone.getText();
                int Age= Integer.parseInt(age.getText());
                 String university = univ.getText();
                 
                String SQL= "UPDATE Students SET Stud_ID=?,Stud_Name=?,Stud_Email=?,Stud_Phone=?,Stud_Age=?,Stud_University=? WHERE Stud_ID=?";
                pstm = con.prepareStatement(SQL);
                pstm.setInt(1, ID);
                pstm.setString(2, Name);
                pstm.setString(3, Email);
                pstm.setString(4, Phone);
                pstm.setInt(5, Age);
                pstm.setString(6, university);
                pstm.setInt(7,ID);

                count = pstm.executeUpdate();
                if(count>0){
                    JOptionPane.showMessageDialog(this, "Updated !", "Success", JOptionPane.INFORMATION_MESSAGE);
                }
                else {
                    JOptionPane.showMessageDialog(this, "ID doesn't exist", "Execution Alert", JOptionPane.ERROR_MESSAGE);
                }

            }catch (SQLException ex) {
                Logger.getLogger(Manage_Librarians.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
          
          else {JOptionPane.showMessageDialog(this, "Email not valid!", "Execution Alert", JOptionPane.ERROR_MESSAGE);}
        }else {
            JOptionPane.showMessageDialog(this, "Fields must be filled!", "Execution Alert", JOptionPane.ERROR_MESSAGE);
        }

    }//GEN-LAST:event_modify_btnActionPerformed

    private void Add_btnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Add_btnActionPerformed
        
        Student stud = new Student();
        
        if(!id.getText().isEmpty() && !name.getText().isEmpty() && !email.getText().isEmpty() && !phone.getText().isEmpty()  &&!age.getText().isEmpty() && !univ.getText().isEmpty())
          if(stud.validateEmail(email.getText()))  {
            try {
                Connection con =  DriverManager.getConnection("jdbc:derby://localhost:1527/Library", "user1", "123" );
                PreparedStatement pstm;
                int count =0;
                int ID= Integer.parseInt(id.getText());
                String Name = name.getText();
                String Email = email.getText();
                String university = univ.getText();
                String Phone = phone.getText();
                int Age= Integer.parseInt(age.getText());
                

                String SQL= "INSERT INTO Students VALUES(?,?,?,?,?,?,?)";

                pstm = con.prepareStatement(SQL);
                pstm.setInt(1, ID);
                pstm.setString(2, Name);
                pstm.setString(3, Email);
                pstm.setString(4, Phone);
                pstm.setInt(5, Age);
                pstm.setString(6, university);
               pstm.setInt(7, 0);
               
              

                count = pstm.executeUpdate();
                con.close();
                if(count>0){
                    JOptionPane.showMessageDialog(this, "Inserted !", "Success", JOptionPane.INFORMATION_MESSAGE);
                }

            }catch (SQLException ex) {
                Logger.getLogger(Manage_Librarians.class.getName()).log(Level.SEVERE, null, ex);
                JOptionPane.showMessageDialog(this, "ID already exists!", "Execution Alert", JOptionPane.ERROR_MESSAGE);

            }
        
          }
          
          else {JOptionPane.showMessageDialog(this, "Email not valid!", "Execution Alert", JOptionPane.ERROR_MESSAGE);}
        else {
            JOptionPane.showMessageDialog(this, "Fields must be filled!", "Execution Alert", JOptionPane.ERROR_MESSAGE);
        }

    }//GEN-LAST:event_Add_btnActionPerformed

