    private void DeleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_DeleteActionPerformed
        if(!id.getText().isEmpty())   
             {
            try { 
               Connection con =  DriverManager.getConnection("jdbc:derby://localhost:1527/Library", "user1", "123" );
               PreparedStatement pstm;
               int count = 0;
               int ID= Integer.parseInt(id.getText());
               String SQL="DELETE FROM Students WHERE Stud_ID = ?";
               pstm = con.prepareStatement(SQL);
               pstm.setInt(1, ID);
               count = pstm.executeUpdate();
               con.close();
               if(count>0){
                   JOptionPane.showMessageDialog(this, "Deleted !", "Success", JOptionPane.INFORMATION_MESSAGE);    
               }
               else{JOptionPane.showMessageDialog(this, "ID doesn't exist", "Execution Alert", JOptionPane.ERROR_MESSAGE);
                    }
            }catch (SQLException ex) {
               Logger.getLogger(Manage_Librarians.class.getName()).log(Level.SEVERE, null, ex);
               }
            }
        else {
          JOptionPane.showMessageDialog(this, "ID must be filled!", "Execution Alert", JOptionPane.ERROR_MESSAGE);    
        }
    }//GEN-LAST:event_DeleteActionPerformed

