    public void FillingTable(){
    
         try {
            Connection con =  DriverManager.getConnection("jdbc:derby://localhost:1527/Library", "user1", "123" );
            String SQL = "SELECT * FROM Students";
            Statement stmt=con.createStatement(ResultSet.TYPE_SCROLL_SENSITIVE,ResultSet.CONCUR_UPDATABLE);  
            ResultSet rs=stmt.executeQuery(SQL);  
            
            while(rs.next()){
                int ID= rs.getInt("Stud_ID");
                String Name = rs.getString("Stud_Name");
                String Email = rs.getString("Stud_Email");
                String Phone = rs.getString("Stud_Phone");
                int Age= rs.getInt("Stud_Age");
                String University = rs.getString("Stud_University");
                
                Object[] obj = {ID,Name,Email,Phone,Age,University};
                model = (DefaultTableModel) Table_Stud.getModel();
                model.addRow(obj);
            
            }
            con.close();
            }
        catch ( SQLException err ) {
            err.printStackTrace();
        }
    
    }
